
$(document).ready(function(){
  //checklist
  (function($) {
	function setChecked(target) {
		var checked = $(target).find("input[type='checkbox']:checked").length;
		if (checked) {
		  $(target).find('select option:first').html('Items: ' + checked);
		  $(target).find('.checkselect-control').addClass('selected');
		} else {
		  $(target).find('select option:first').html('Select items');
          $(target).find('.checkselect-control').removeClass('selected');
		}
	}
 
	$.fn.checkselect = function() {
		this.wrapInner('<div class="checkselect-popup"></div>');
		this.prepend(
			'<div class="checkselect-control">' +
				'<select class="form-control"><option></option></select>' +
				'<div class="checkselect-over"></div>' +
			'</div>'
		);	
 
		this.each(function(){
			setChecked(this);
		});		
		this.find('input[type="checkbox"]').click(function(){
			setChecked($(this).parents('.checkselect'));
		});
 
		this.parent().find('.checkselect-control').on('click', function(){
			$popup = $(this).next();
            $(this).addClass('open');
			$('.checkselect-popup').not($popup).css('display', 'none');
			if ($popup.is(':hidden')) {
				$popup.css('display', 'block');
				$(this).find('select').focus();
			} else {
				$popup.css('display', 'none');
			}
		});
 
		$('html, body').on('click', function(e){
			if ($(e.target).closest('.checkselect').length == 0){
		      $('.checkselect-popup').css('display', 'none');
              $('.checkselect-popup').prev().removeClass('open');
			}
		});
	};
  })(jQuery); 
  $('.checkselect').checkselect();
  
  //notnum input block
  function Ftest (obj){
    if (this.ST) return; var ov = obj.value;
       var ovrl = ov.replace (/\d*\.?\d*/, '').length; 
       this.ST = true;
       if (ovrl > 0) {
           obj.value = obj.lang; 
           Fshowerror (obj); 
           return
       }
       obj.lang = obj.value; 
       this.ST = null;
  }
  function Fshowerror (obj){
      if (!this.OBJ){
          this.OBJ = obj; 
          this.TIM = setTimeout (Fshowerror, 50);
      } else {
          clearTimeout (this.TIM); 
          this.ST = null; 
          Ftest (this.OBJ); 
          this.OBJ = null;
      }
  }
  $('.js-onlynumb').on('input', function(e){
    Fshowerror (e.target);
  });
  
  //search hint
  $('.js-search-input').on('input focus', function(){
    if ($.trim($(this).val())) {
        getSearchedContestName();
      $(this).next().slideDown(250);
    } else{
      $(this).next().slideUp(250);
    }
  });
  $('.js-search-input').on('focusout', function(){
    $(this).next().slideUp(250);
  });  
  $('.js-search-list li').on('click', function(){
    $(this).closest('.js-search').find('.js-search-input').val($(this).text());
    $(this).closest('.js-search-list').slideUp(250);
  });
  
  //styled selects
  var x, i, j, selElmnt, a, b, c;
  x = document.getElementsByClassName("js-styled-select");
  for (i = 0; i < x.length; i++) {
    selElmnt = x[i].getElementsByTagName("select")[0];
    a = document.createElement("DIV");
    a.setAttribute("class", "select-selected");
    a.innerHTML = 'Sort by: ' + selElmnt.options[selElmnt.selectedIndex].innerHTML;
    x[i].appendChild(a);
    b = document.createElement("DIV");
    b.setAttribute("class", "select-items select-hide");
    for (j = 0; j < selElmnt.length; j++) {
      c = document.createElement("DIV");
      if(j === 0){
        c.classList.add('same-as-selected');
      }
      c.innerHTML = selElmnt.options[j].innerHTML;
      c.addEventListener("click", function(e) {
          var y, i, k, s, h;
          s = this.parentNode.parentNode.getElementsByTagName("select")[0];
          h = this.parentNode.previousSibling;
          for (i = 0; i < s.length; i++) {
            if (s.options[i].innerHTML == this.innerHTML) {
              s.selectedIndex = i;
              h.innerHTML = 'Sort by: ' + this.innerHTML;
              y = this.parentNode.getElementsByClassName("same-as-selected");
              for (k = 0; k < y.length; k++) {
                y[k].removeAttribute("class");
              }
              this.setAttribute("class", "same-as-selected");
                sendFilterRequest();
              break;
            }
          }
          h.click();
      });
      b.appendChild(c);
    }
    x[i].appendChild(b);
    a.addEventListener("click", function(e) {
      e.stopPropagation();
      closeAllSelect(this);
      this.nextSibling.classList.toggle("select-hide");
      this.classList.toggle("select-arrow-active");
    });
  }
  function closeAllSelect(elmnt) {
      if (elmnt.path[0].className == "search__item"){
          document.getElementsByClassName("js-search-input")[0].value = elmnt.path[0].innerText;
      }
    var x, y, i, arrNo = [];
    x = document.getElementsByClassName("select-items");
    y = document.getElementsByClassName("select-selected");
    for (i = 0; i < y.length; i++) {
      if (elmnt == y[i]) {
        arrNo.push(i)
      } else {
        y[i].classList.remove("select-arrow-active");
      }
    }
    for (i = 0; i < x.length; i++) {
      if (arrNo.indexOf(i)) {
        x[i].classList.add("select-hide");
      }
    }
  }
  document.addEventListener("click", closeAllSelect);
  
  //show mor
  $('.js-show-mor').on('click', function(){
    if($(this).hasClass('open')){
      $(this).prev().slideUp(250);
      $(this).removeClass('open');
      $(this).find('span').text('Show more');
    } else{
      $(this).prev().slideDown(250);
      $(this).addClass('open');
      $(this).find('span').text('Show less');
    }
  });
  
  //input changes
  $('.js-fromto').on('focus', function(){
    $(this).parent().addClass('focused');
  });
  $('.js-fromto').on('input', function(){
    if ($.trim($(this).val())) {
      $(this).parent().addClass('value');
    } else{
      $(this).parent().removeClass('value');
    }
  });
  $('.js-fromto').on('focusout', function(){
    $(this).parent().removeClass('focused');
  });

});


